/**
 * Customize latest post on home page
 */
function latest_post_class( $postid ) {

	$last 		= wp_get_recent_posts( array( 'numberposts' => '1', 'post_status' => 'publish' ) );
	$last_id 	= $last['0']['ID'];
	$class		= '';

	if ( $postid == $last['0']['ID']  ) {

		$class = 'latest_post';

	}

	return $class;

} // End of latest_post_class()


add_filter( 'use_default_gallery_style', '__return_false' );

/**
 * Sets the except length to 30
 * 
 * @param  [type] $length [description]
 * 
 * @return [type]         [description]
 */
function custom_excerpt_length( $length ) {

	return 30;

} // End of custom_excerpt_length()

add_filter( 'excerpt_length', 'custom_excerpt_length', 999 );


function craft_excerpt( $count ) {

	$permalink 	= get_permalink( get_the_ID() );
  	$excerpt 	= get_the_content();
  	$excerpt 	= strip_tags( $excerpt );

  	if ( strlen( $excerpt ) > $count ) {

  		$excerpt = substr( $excerpt, 0, $count );
  		$excerpt = $excerpt . ' ... <a href="' . $permalink . '">more</a>';

  	}

  	return $excerpt;

} // End of craft_excerpt()

/**
 * Sets the limit of posts on the home page to 5
 * 
 * @param  	mixed 		$query 		The query on the home page
 * @return 	mixed        			?
 */
function craft_home_posts( $query ) {

	if ( is_home() && $query->is_main_query() ) {

		$query->set( 'posts_per_page', 5 );
		$query->set( 'category_name', 'Podcast' );
		return;

	}

} // End of craft_home_posts()

add_action( 'pre_get_posts', 'craft_home_posts', 1 );

/**
 * Changes the placeholder text
 * 
 * @param  	string 		$title 		The current placeholder text
 *
 * @uses 	get_current_screen()
 * 
 * @return 	string        			The new placeholder text
 */
function change_default_title( $title ){
 
	$screen = get_current_screen();
 
	if ( 'post' == $screen->post_type ){
  
		$title = 'Enter guest name here';

	}

	return $title;

} // End of change_default_title()

add_filter( 'enter_title_here', 'change_default_title' );